/**
 * Token class
 * Represents tokens generated by lexer
 * <p>
 *
 * @author  Daniel Jackson
 * @version 0, 07/06/01
 */

//package tagger;
//import java.io.*;
//import java.util.*;

public class Token {
  // may be null
  public String arg;
  public int line;
  public int type;

  public static final int COMMENT              () { return  0;  }
  public static final int WHITESPACE           () { return  1;	}
  public static final int ALPHABETIC           () { return  2;	}
  public static final int NUMERIC              () { return  3;	}

  public static final int PARABREAK            () { return  4;	}
  public static final int LINEBREAK            () { return  5;	}

  public static final int APOSTROPHE           () { return  10;	}
  public static final int PRIME                () { return  11;	}
  public static final int OPENSINGLEQUOTE      () { return  12;	}
  public static final int CLOSESINGLEQUOTE     () { return  13;	}
  public static final int OPENDOUBLEQUOTE      () { return  14;	}
  public static final int CLOSEDOUBLEQUOTE     () { return  15;	}
  public static final int HYPHENS              () { return  16;	}
  public static final int DOTS                 () { return  17;	}

  public static final int PARASTYLECOMMAND     () { return  20;	}
  public static final int FORMATCOMMAND        () { return  21;	}
  public static final int POPFORMATCOMMAND     () { return  22;	}
  public static final int REFCOMMAND           () { return  23;	}
  public static final int TAGCOMMAND           () { return  24;	}
  public static final int CITECOMMAND          () { return  25;	}
  public static final int CHARCOMMAND          () { return  26;	}
  public static final int LOADCHARMAPCOMMAND   () { return  27;	}
  public static final int LOADSTYLESHEETCOMMAND() { return  28;	}
  public static final int PREAMBLECOMMAND      () { return  29;	}
  public static final int SEPARATORCOMMAND     () { return  30;	}

  public static final int UNDERSCORE           () { return  31;	}
  public static final int DOLLAR               () { return  32;	}
  public static final int OTHER                () { return  33;	}
  public static final int ENDOFSTREAM          () { return  34;	}

  public static final int MAXTOKEN             () { return  34; }
  
  public Token (int type, String arg, int line) {
    this.type = type;
    this.arg = arg;
    this.line = line;
  }
  
  public Token (int type, int line) {
    this.type = type;
    this.line = line;
  }

  // temporary implementation
  public String toString () {
    return arg;
  }
}
